mapper.update:
UPDATE Inquiry
SET boardHit = boardHit + 1
WHERE boardNum = #{boardNum}

UPDATE Inquiry
SET boardLike = boardLike + 1
WHERE boardNum = #{boardNum}

UPDATE Inquiry
SET boardLike = #{boardLike}
WHERE boardNum = #{boardNum}

UPDATE Inquiry
SET answerCnt = #{answerCnt}
WHERE boardNum = #{boardNum}

UPDATE Inquiry
SET isSelected = 1
WHERE boardNum = #{boardNum}
