mapper.update:
UPDATE PetsitterReply SET
replyContent = #{replyContent}
WHERE replyNum = #{replyNum}

mapper.select:
SELECT COUNT(*)
FROM PetsitterReply
WHERE boardNum = #{boardNum}

mapper.update:
UPDATE PetsitterReply
SET replyOrder = replyOrder + 1
WHERE replyGID = #{replyGID} and replyOrder > #{replyOrder}
